import React, { useState, useEffect } from 'react'; 
import { Grid, Button, TextField } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import TextInput from './TextInput';
import RadioInput from './RadioInput';
import SelectInput from './SelectInput';
import { countryOptions, countryToFlag } from './Countries';

const initialFormValues = {
    firstName: '', 
    lastName: '', 
    emailAddress: '', 
    streetAddress: '', 
    city: '', 
    state: '',
    country: 'United States', 
    zip: '', 
    phoneNumber: '', 
    dateOfBirth: '', 
    gender: 'male'
}

const genderOptions = [
    {value: 'male', label: 'Male'}, 
    {value: 'female', label: 'Female'}, 
    {value: 'other', label: 'Other'}
]

const stateOptions = [
    { code: 'CA', label: 'California' },
    { code: 'MA', label: 'Massachusetts' },
    { code: 'NY', label: 'New York' }
]

const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
      textAlign: '-webkit-center',
    },
    multipleItems: {
        display: 'inline-flex',
        justifyContent: 'center', 
    },
    padLeft: {
        marginLeft: '10px',
    }, 
    padRight: {
        marginRight: '10px',
    }
  }));

const handleSubmit = () => {

}

const ValidationForm = () => {
    const [values, setValues] = useState(initialFormValues); 
    const classes = useStyles(); 

    const handleInputChange = (e) => {
        const {name, value} = e.target; 
        setValues({
            ...values,
            [name]: value
        });
    }

    return (
    <form className={classes.root} onSubmit={handleSubmit} autoComplete="off">
    <Grid container spacing={2}>
        <Grid item xs={12}> <h3>Welcome!</h3> </Grid>
        <Grid item xs={12} className={classes.multipleItems}>
            <TextInput 
                label="First Name" 
                name="firstName"
                value={values.firstName}
                style={classes.padRight} 
                onChange={handleInputChange}
            />
            <TextInput 
                label="Last Name" 
                name="lastName"
                value={values.lastName}
                style={classes.padLeft}
                onChange={handleInputChange}
            />
        </Grid>
        <Grid item xs={12}>
            <TextInput 
                label="Email Address"
                name="emailAddress"
                value={values.emailAddress}
                onChange={handleInputChange}
            />
        </Grid>
        <Grid item xs={12}>
            <TextInput 
                label="Street Address"
                name="streetAddress"
                value={values.streetAddress}
                onChange={handleInputChange}
            />
        </Grid>
        <Grid item xs={12} className={classes.multipleItems}>
            <TextInput 
                label="City" 
                name="city"
                value={values.city}
                style={classes.padRight}
                onChange={handleInputChange}
            />
            <SelectInput
                options={stateOptions}
                getOptionLabel={(option) => option.label}
                renderInput={(params) => 
                    <TextField
                        {...params}
                        label="Choose a state/province"
                        variant="outlined"
                    />
                }
                style={{ width: 300 }}
                classes={classes.padLeft}
            />
        </Grid>
        <Grid item xs={12} className={classes.multipleItems}>
            <SelectInput
                options={countryOptions}
                getOptionLabel={(option) => option.label}
                renderOption={(option) => 
                    <React.Fragment>
                        <span>{countryToFlag(option.code)}</span>
                        {option.label} ({option.code})
                    </React.Fragment>
                }
                renderInput={(params) => 
                    <TextField
                        {...params}
                        label="Choose a country/region"
                        variant="outlined"
                    />
                }
                style={{ width: 300 }}
                classes={classes.padRight, countryClasses.option}
            />
            <TextInput 
                label="Zip/Postal" 
                name="zip"
                value={values.zip}
                style={classes.padLeft}
                onChange={handleInputChange}
            />
        </Grid>
        <Grid item xs={12}>
            <TextInput 
                label="Phone Number"
                name="phoneNumber"
                value={values.phoneNumber}
                onChange={handleInputChange}
            />
        </Grid>
        <Grid item xs={12}>
            <TextInput 
                label="Date of Birth"
                name="dateOfBirth"
                value={values.dateOfBirth}
                onChange={handleInputChange}
            />
        </Grid>
        <Grid item xs={12}>
            <RadioInput 
                name="gender"
                value={values.gender}
                onChange={handleInputChange}
                options={genderOptions}
            />
        </Grid>
        <Grid item xs={12}>
            <Button 
                variant="outlined"
            >
                Get my free samples
            </Button>
        </Grid>
    </Grid>
    </form>
)
}

export default ValidationForm;
