import { makeStyles, TextField } from '@material-ui/core'
import { Autocomplete } from '@material-ui/lab';
import React from 'react'
import { countryOptions, countryToFlag } from './Countries'
// import SelectInput from './SelectInput'

const countryUseStyles = makeStyles({
    option: {
        fontSize: 15,
        '& > span': {
        marginRight: 10,
        fontSize: 18,
        },
    },
});

const convertEventParameter = (name, value) => ({
    target: {
        name, value
    }
})

export default function CountrySelectInput(props) {
    const {name, value, onChange} = props; 
    const countryClasses = countryUseStyles(); 
    return (
        // <SelectInput
        //         options={countryOptions}
        //         getOptionLabel={(option) => option.label}
        //         renderOption={(option) => 
        //             <React.Fragment>
        //                 <span>{countryToFlag(option.code)}</span>
        //                 {option.label} ({option.code})
        //             </React.Fragment>
        //         }
        //         renderInput={(params) => 
        //             <TextField
        //                 {...params}
        //                 label="Choose a country/region"
        //                 variant="outlined"
        //             />
        //         }
        //         style={{ width: 300 }}
        //         classes={{option: countryClasses.option}}
        //         name={name}
        //         value={value}
        //         onChange={onChange}
        // />
        <Autocomplete
            style={{width: 300}}
            options={countryOptions}
            classes={{option: countryClasses.option}}
            autoHighlight
            getOptionSelected={(option, value) => console.log(option, 'value: ', value)}
            getOptionLabel={(option) => option.label || ""}
            renderOption={(option) => 
                <React.Fragment>
                    <span>{countryToFlag(option.code)}</span>
                    {option.label} ({option.code})
                </React.Fragment>}
            renderInput={(params) => 
                <TextField
                    {...params}
                    label="Choose a country/region"
                    variant="outlined"
                    inputProps={{
                        ...params.inputProps,
                        autoComplete: 'new-password', // disable autocomplete and autofill
                    }}
                />}
            // name={name}
            // value={value}
            onChange={(e, v) => onChange(convertEventParameter(name, v.label))}
        />
    )
}
