import React, { useState, useEffect } from 'react'; 
import { TextField, Grid, RadioGroup, Radio, FormControlLabel, FormControl, FormLabel, Button } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import CountrySelect from './CountrySelect';

const initialFormValues = {
    firstName: '', 
    lastName: '', 
    emailAddress: '', 
    streetAddress: '', 
    city: '', 
    state: '',
    country: 'United States', 
    zip: '', 
    phoneNumber: '', 
    dateOfBirth: '', 
    gender: 'female'
}

const OutlinedTextField = (props) => {
    return (
      <TextField 
        id="outlined-basic" 
        variant="outlined" 
        label={props.label} 
        value={props.value}
        className={props.style}
        helperText={props.helperText}
        onChange={props.onChange}
      />
    )
}

const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
      textAlign: '-webkit-center',
    },
    multipleItems: {
        display: 'inline-flex',
        justifyContent: 'center', 
    },
    padLeft: {
        marginLeft: '10px',
    }, 
    padRight: {
        marginRight: '10px',
    }
  }));

const handleSubmit = () => {

}

const ValidationForm = () => {
    const [values, setValues] = useState(initialFormValues); 
    const classes = useStyles(); 

    const handleInputChange = (e) => {
        const {name, value} = e.target; 
        setValues({
            ...values,
            [name]: value
        });
    }

    return (
    <form className={classes.root} onSubmit={handleSubmit}>
    <Grid container spacing={2}>
        <Grid item xs={12}> <h3>Welcome!</h3> </Grid>
        <Grid item xs={12} className={classes.multipleItems}>
            <OutlinedTextField 
                label="First Name" 
                name="firstName"
                value={values.firstName}
                style={classes.padRight} 
                onChange={handleInputChange}
            />
            <OutlinedTextField 
                label="Last Name" 
                value={values.lastName}
                style={classes.padLeft}
            />
        </Grid>
        <Grid item xs={12}>
            <OutlinedTextField 
                label="Email Address"
                value={values.emailAddress}
            />
        </Grid>
        <Grid item xs={12}>
            <OutlinedTextField 
                label="Street Address"
                value={values.streetAddress}
            />
        </Grid>
        <Grid item xs={12} className={classes.multipleItems}>
            <OutlinedTextField 
                label="City" 
                value={values.city}
                style={classes.padRight}
            />
            <CountrySelect 
                value={values.state}
                style={classes.padLeft}
            />
        </Grid>
        <Grid item xs={12} className={classes.multipleItems}>
            <CountrySelect 
                value={values.country}
                style={classes.padRight}
            />
            <OutlinedTextField 
                label="Zip/Postal" 
                value={values.zip}
                style={classes.padLeft}
            />
        </Grid>
        <Grid item xs={12}>
            <OutlinedTextField 
                label="Phone Number"
                value={values.label}
            />
        </Grid>
        <Grid item xs={12}>
            <OutlinedTextField 
                label="Date of Birth"
                value={values.dateOfBirth}
            />
        </Grid>
        <Grid item xs={12}>
            <FormControl component="fieldset">
                <FormLabel component="legend">Gender</FormLabel>
                <RadioGroup aria-label="gender" name="gender1">
                    <FormControlLabel value="female" control={<Radio />} label="Female" />
                    <FormControlLabel value="male" control={<Radio />} label="Male" />
                    <FormControlLabel value="other" control={<Radio />} label="Other" />
                </RadioGroup>
            </FormControl>
        </Grid>
        <Grid item xs={12}>
            <Button 
                variant="outlined"
            >
                Get my free samples
            </Button>
        </Grid>
    </Grid>
    </form>
)
}

export default ValidationForm;
