import React, { useState, useEffect } from 'react'; 
import { Grid, RadioGroup, Radio, FormControlLabel, FormControl, FormLabel, Button } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import TextInput from './TextInput';
import CountrySelect from './CountrySelect';

const initialFormValues = {
    firstName: '', 
    lastName: '', 
    emailAddress: '', 
    streetAddress: '', 
    city: '', 
    state: '',
    country: 'United States', 
    zip: '', 
    phoneNumber: '', 
    dateOfBirth: '', 
    gender: 'female'
}

const useStyles = makeStyles((theme) => ({
    root: {
      flexGrow: 1,
      textAlign: '-webkit-center',
    },
    multipleItems: {
        display: 'inline-flex',
        justifyContent: 'center', 
    },
    padLeft: {
        marginLeft: '10px',
    }, 
    padRight: {
        marginRight: '10px',
    }
  }));

const handleSubmit = () => {

}

const ValidationForm = () => {
    const [values, setValues] = useState(initialFormValues); 
    const classes = useStyles(); 

    const handleInputChange = (e) => {
        const {name, value} = e.target; 
        setValues({
            ...values,
            [name]: value
        });
    }

    return (
    <form className={classes.root} onSubmit={handleSubmit}>
    <Grid container spacing={2}>
        <Grid item xs={12}> <h3>Welcome!</h3> </Grid>
        <Grid item xs={12} className={classes.multipleItems}>
            <TextInput 
                label="First Name" 
                name="firstName"
                value={values.firstName}
                style={classes.padRight} 
                onChange={handleInputChange}
            />
            <TextInput 
                label="Last Name" 
                name="lastName"
                value={values.lastName}
                style={classes.padLeft}
                onChange={handleInputChange}
            />
        </Grid>
        <Grid item xs={12}>
            <TextInput 
                label="Email Address"
                name="emailAddress"
                value={values.emailAddress}
                onChange={handleInputChange}
            />
        </Grid>
        <Grid item xs={12}>
            <TextInput 
                label="Street Address"
                name="streetAddress"
                value={values.streetAddress}
                onChange={handleInputChange}
            />
        </Grid>
        <Grid item xs={12} className={classes.multipleItems}>
            <TextInput 
                label="City" 
                name="city"
                value={values.city}
                style={classes.padRight}
                onChange={handleInputChange}
            />
            <CountrySelect 
                value={values.state}
                style={classes.padLeft}
            />
        </Grid>
        <Grid item xs={12} className={classes.multipleItems}>
            <CountrySelect 
                value={values.country}
                style={classes.padRight}
            />
            <TextInput 
                label="Zip/Postal" 
                name="zip"
                value={values.zip}
                style={classes.padLeft}
                onChange={handleInputChange}
            />
        </Grid>
        <Grid item xs={12}>
            <TextInput 
                label="Phone Number"
                name="phoneNumber"
                value={values.phoneNumber}
                onChange={handleInputChange}
            />
        </Grid>
        <Grid item xs={12}>
            <TextInput 
                label="Date of Birth"
                name="dateOfBirth"
                value={values.dateOfBirth}
                onChange={handleInputChange}
            />
        </Grid>
        <Grid item xs={12}>
            <FormControl>
                <FormLabel>Gender</FormLabel>
                <RadioGroup row
                    name="gender"
                    value={values.gender}
                    onChange={handleInputChange}
                >
                    <FormControlLabel value="female" control={<Radio />} label="Female" />
                    <FormControlLabel value="male" control={<Radio />} label="Male" />
                    <FormControlLabel value="other" control={<Radio />} label="Other" />
                </RadioGroup>
            </FormControl>
        </Grid>
        <Grid item xs={12}>
            <Button 
                variant="outlined"
            >
                Get my free samples
            </Button>
        </Grid>
    </Grid>
    </form>
)
}

export default ValidationForm;
